sys_ui_page.client_script:
var emptyOption = {
	html: 'None',
	value: '',
	selected: true
};
//$j("#fieldType").append($j("#fieldType").append($j("<option>", emptyOption)));

loadFieldTypes();

function buildAjax(scriptInclude, functionName, paramMap){
	var ajaxCall = new GlideAjax(scriptInclude);
	ajaxCall.addParam("sysparm_name", functionName);
	if (paramMap)
		for (param in paramMap)
			ajaxCall.addParam("sysparm_"+param, paramMap[param]);
	return ajaxCall;

}

function loadFieldTypes(){
	var fieldTypeSelect = $j("#fieldType");
	buildAjax("swe_searchEngine", "getFieldTypes").getXMLAnswer(function(answer){
		var options = answer.split(",$");
		alert(options);
		for (var i=0;i<options.length;i++){
			$j("#fieldType").append($j("#fieldType").append($j("<option>", {
				html:options[i], 
				value:'', 
				selected:true
			})));
		}
	});

}
function run(){
	alert("ran");
}

function goBack(){
	window.history.back();
}
